import type { BaseEngine } from "./BaseEngine";
/**
 * A checkpoint used for a LoRA Tune.
 */
export type LoraTuneCheckpoint = {
    /**
     * The checkpoint asset ID.
     */
    checkpoint_id: string;
    /**
     * Tune engine.
     */
    engine?: BaseEngine | null;
    /**
     * The name of the checkpoint. This is cached from asset lake in case the asset is deleted.
     */
    name?: string | null;
};
